package operator;

public class P85P {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 0B00000101;
		System.out.println(num<< 2);
		System.out.println(num>> 2);
		System.out.println(num>>> 2);
		
		num =num <<2;
		System.out.println(num);
	}

}
